import React, {Component} from 'react';
import MerriamAPI from '../services/MerriamAPI';


class WordDetails extends Component {

  state = {
      currentWord: {},
      info: [],
      partOfSpeech: [],
      versions: [],
      shortdef: "",
      pronunciation: "",
      error: '',
  };

  async componentDidUpdate(prevProps, prevState) {
    const word = this.props.currentWord;
    if (word !== prevProps.currentWord) {
      const currentWord = await MerriamAPI.getWordInfo(word);
      this.setState({ 
        currentWord: currentWord,
        info: data,
        versions: data[0].meta.stems,
        shortdef: data[0].shortdef[0],
        partOfSpeech: data[0].fl,
        pronunciation: data[0].hwi.prs[0].mw, 
        error: '',
       });
    }
  }


render(){
    let detail; 
    if (this.state.word.id){
        detail = 
        <div>
            <p>      
                <b>part of speech:</b> {this.state.partOfSpeech}
            </p>
            <p>
                <b>pronunciation:</b> {this.state.pronunciation}
            </p>
            <p>
                <b>definition:</b>  {this.state.shortdef}
            </p>
            <p>
                <b>versions:</b>  {this.state.versions.map((v, i) => <div key={i}>{v}</div>)}
            </p>
        </div>
    }
    else 
        detail = 
            <div>
                <span>No word selected</span>
            </div>

    return (
        <div>
            {detail}
        </div>
    );
  } 
};

export default WordDetails;
