import React, {Component} from 'react';

class Search extends Component {

// state = {
//   word: ''
// }


handleChange = (event) => {
  console.log(`Filter changed! ${event.target.value}`);
  this.props.word = event.target.value.toLowerCase();
  }


handleSubmit = (e) => {
  e.preventDefault();
  var value = this.props.word.trim();
  console.log(value)
}

render (){
  const {word} = this.props;
    return (
      <form onSubmit={this.handleSubmit}>
      <div>
        <input type="text" onChange={this.handleChange} value={word}/>
        <button type="submit">Search</button>
      </div>
      </form>
    );
  }
}

export default Search;
