import React, {Component} from 'react';
import {Switch, Route, Link, Redirect} from 'react-router-dom';
import MerriamAPI from '../services/MerriamAPI';

class Search extends Component {


state = {
  info: [],
  word: '',
  error: ''
}

// handleChange = (event) => {
//   this.setState({word:event.target.value.toLowerCase()})
// }

handleChange = (event) => {
  const { onUpdate } = this.props;
  const val = event.target.value.toLowerCase();
  this.setState({word: val}, () => { onUpdate(val); })
}


handleSubmit = async (e) => {
  try {
    e.preventDefault();
    var word = this.state.word.trim();
    const data = await MerriamAPI.getWordInfo(word);
    if (data){
      this.setState({info: data[0].shortdef[0]})
      // this.props.history.push("/definition");
    }
    else {
      console.log('No Definition Found')
    }
  }
  catch{
    this.setState({error: 'No Definition Found'})
  }
  // console.log(this.state.info[0].meta.id)
  // console.log(this.state.info[0].shortdef)
}


render (){
  if (!this.state.error && this.state.info.length > 0) {
    return <Redirect to={{ pathname: `/definition/${this.state.word}`, state: { info: this.state.info } }} />
  }
    return (
      <form onSubmit={this.handleSubmit}>
      <div>
        <input type="text" onChange={this.handleChange}/>
        <button type="submit">Search</button>
        <p>Definition:</p>
        <div>
          {this.state.info}
          {this.state.error}
          {/* {this.state.info.map((info) => <p key={info[0].meta.id}>{...info}</p>)}  */}
        </div>
      </div>
      </form>
    );
  }
}



export default Search;
