          setModal={this.props.setModal}
        />
        {this.state.error}
        </div>
        <div >
        <h1 className="display-6">
          definition
        </h1>
        <div>
             <b>word:</b> {this.state.word}
        </div>
        <div>
              <b>part of speech:</b> {this.state.partOfSpeech}
        </div>
