import React, {Component} from 'react';
import Search from '../components/Search';
import {Switch, Route, Link, Redirect, withRouter} from 'react-router-dom';
import MerriamAPI from '../services/MerriamAPI';

class Home extends Component {   

    state = {
        word: {}
    }

    setWord = word =>
        this.setState({word});

    callApi = () => {
        return MerriamAPI.getWordInfo(this.state.word)
    }

    redirectTo = (word) => 
        // `/definition/${word}`
        `/definition/jovial`


    render (){
        // if (!this.state.error && this.state.submitted && this.state.info.length > 0) {
        // return <Redirect to={{ pathname: `/definition/${this.state.word}`, state: { word: this.state.word } }} />
        // }
        return (
            <div>
            <h1>My Dictionary</h1>
            <Search 
            word={this.state.word}
            setWord={this.setWord}
            callApi={this.callApi}
            redirectTo={this.props.setRedirect}
            />
            </div>
        );
    }
}








