import React, {Component} from 'react';
import Search from '../components/Search';
import MerriamAPI from '../services/MerriamAPI';

class Home extends Component {   

    constructor(){ 
        super();
          this.state = {
            info: [],
            // word: '',
            error: ''
          };
        this.search = this.search.bind(this);
      }

    search = async ({word}) => {
        try{
            // var word = this.state.word.trim();
            const data = await MerriamAPI.getWordInfo(word);
            if (data){
                this.setState({
                    // word: this.state.word,
                    info: data[0].shortdef[0]
                })
            }
            else{
                console.log('No Definition Found')
            }
        }
        catch{
            this.setState({error: 'No Definition Found'})
        }
        console.log(this.state.info)
    }


render (){
    if (!this.state.error && this.state.word.length > 0) {
    return <Redirect to={{ pathname: `/definition/${this.state.word}`, state: { word: this.state.word } }} />
    }
    return (
        <div>
        <h1>My Dictionary</h1>
        <Search search={this.search}/>
        <div>
             {this.state.info}
             {this.state.error}
        </div>
        </div>
    );
    }
}

export default Home;
