import React, {Component} from 'react';
import WordRow from '../../components/WordRow';
import styles from './Saved.module.css';

class Saved extends Component {   

//   state =  {
//       current: {},
//   } 

//   handleDetailsClick = word => {
//     console.log(`Fetching details for ${word.word}`);
//     this.setState({ current: word });
//   }

    render (){

        const allWords = this.props.faves.map(word =>
            <WordRow
                key={word.word}
                word={word}
                isFave={this.props.faves.word=(this.props.word)}
                handleSave={() => this.props.handleSave(word)}
            />
        );

        return (
            <div className={styles['saved-background']}>
            <h1 className={styles['first-header']}>My Saved Words: {this.props.faves.length}</h1>
            {allWords}
            {/* <WordDetails word={this.state.current} /> */}
            </div>
        );
    }
}

export default Saved;
