import React, {Component} from 'react';
import Search from '../components/Search';
import {Switch, Route, Link, Redirect, withRouter} from 'react-router-dom';
import MerriamAPI from '../services/MerriamAPI';


class GetWordContainer extends Component {

  search = async ({word}) => {
    try{
        // var word = this.state.word.trim();
        const data = await MerriamAPI.getWordInfo(word);
        if (data){
            this.setState({
                word: word,
                info: data[0].shortdef[0]
            }, 
            () => {
                this.props.history.push(`/definition/${this.state.word}`);
            }
            );
        }
        else{
            console.log('No Definition Found')
        }
    }
    catch{
        this.setState({error: 'No Definition Found'})
    }
    // console.log(this.state.info)
    // console.log(this.state.word)
}

  state = {
    word: '',
    info: [],
  }

async componentDidMount(){
  try{
    const {data} = await MerriamAPI.show(this.props.match.params.word);
    if (data){
      this.setState({info: data})
    }
    else{
      console.log("ERROR ON GETWORDCONTAINER!")
    }
  }
  catch{
    console.log("ERROR ON GETWORDCONTAINER!")
  }
  console.log(this.state.info)
}


render (){

  return (
      <div>
      <Search />
      <div>
          {/* expected results */}
          {/* {this.state.info} */}
      </div>
      </div>
  );
  }
}

 export default GetWordContainer;
