const apiKey = process.env.REACT_APP_MERRIAM_API_KEY;
const baseUrl = 'https://www.dictionaryapi.com/api/v3/references/collegiate/json';

// const handleAPIErrors = response => {

// }

function getQueryParams(params){
    params["key"] = apiKey;
        var pairs = Object.keys(params).map(function(key){
            var result = key + "=" + params[key];
            return result;
        }) 
        var urlParams = pairs.reduce(function(prev,next){
            return prev + "&" + next
        })  
    return urlParams
}

function prepareUrl(word, params){
    var url = baseUrl + "/" + word + "?" + getQueryParams(params);
    return url
}


function getWordInfo(word, params){
    var params = {}
    var url = prepareUrl(word, params);
    console.log(url)
    return fetch(url).then(response => response.json());
}

const MerriamAPI = {
    getDefinitions,
    getWordInfo,
}

export default MerriamAPI;
