import React, {Component} from 'react';
import Search from '../components/Search';
import {Switch, Route, Link, Redirect, withRouter} from 'react-router-dom';
import MerriamAPI from '../services/MerriamAPI';

class Home extends Component {   

    constructor(){ 
        super();
          this.state = {
            word: '',
            error: '',
            info: [],
            versions: [],
            shortdef: "",
            pronunciation: "",
          };
        this.search = this.search.bind(this);
      }

    search = async ({word}) => {
        try{
            // var word = this.state.word.trim();
            const data = await MerriamAPI.getWordInfo(word);
            if (data){
                this.setState({
                    word: word,
                    info: data,
                    versions: data[0].meta.stems,
                    shortdef: data[0].shortdef[0],
                    partOfSpeech: data[0].fl,
                    pronunciation: data[0].hwi.prs[0].mw,
                }, 
                // () => {
                //     this.props.history.push(`/definition/${this.state.word}`);
                // }
                );
            }
            else{
                console.log('No Definition Found')
            }
        }
        catch{
            this.setState({error: 'No Definition Found'})
        }
        // console.log(this.state.info)
        // console.log(this.state.word)
    }


render (){
    // if (!this.state.error && this.state.submitted && this.state.info.length > 0) {
    // return <Redirect to={{ pathname: `/definition/${this.state.word}`, state: { word: this.state.word } }} />
    // }
    return (
        <div>
        <h1>My Dictionary</h1>
        <Search search={this.search}/>
        <div>
             {this.state.error}
        </div>
        <div>
             {this.state.word}
        </div>
        <div>
             {this.state.versions}
        </div>
        <div>
             {this.state.pronunciation}
        </div>
        <div>
             {this.state.shortdef}
        </div>
        </div>
    );
    }
}



export default Home;
