import React, {Component} from 'react';
import Search from '../components/Search';
import { Router } from 'react-router-dom';
import MerriamAPI from '../services/MerriamAPI';

 class GetWordContainer extends Component {

    state = {
        word: 'jovial'
    }


    state = {
        info: {}
      }

      async componentDidMount() {
          const data = await MerriamAPI.index();
          this.setState({info: data});
          console.log(this.setState)
      }

    // getInitialState = () => {
    //     return{
    //         word: ''
    //     }
    // }

    handleUpdateWord = (event) => {
        console.log(`Filter changed! ${event.target.value}`);
        this.setState({word:event.target.value.toLowerCase()
        })
    }

    getDefinition = (word) =>{
        this.setState({
            word: ''
        });
        this.context.router.push({
            pathname: "/definitions/" + word,
        })
    }

    handleSubmitWord = (e) => {
        e.preventDefault();
        var word = this.state.word.trim();
        this.getDefinition(word);
    }


   render() {
     return (
       <>
       <Search
            word={this.state.word}
            onUpdateWord={this.handleUpdateWord}
            onSubmitWord={this.handleSubmitWord}
        />
       </>
     );
   }
 }

 export default GetWordContainer;
