import React, {Component} from 'react';

class Search extends Component {

state = {
  word: ''
}


handleChange = (event) => {
  console.log(`Filter changed! ${event.target.value}`);
  this.props.setWord({
    ...this.props.word,[event.target.value]:toLowerCase()
  });
}


handleSubmit = (e) => {
  e.preventDefault();
  var word = this.state.word.trim();
  console.log(word)
}

render (){
  const {word} = this.props;
    return (
      <form onSubmit={this.handleSubmit}>
      <div>
        <input type="text" onChange={this.handleChange} value={this.props.word}/>
        <button type="submit">Search</button>
      </div>
      </form>
    );
  }
}

export default Search;
