import React, {Component} from 'react';
import Search from '../components/Search';
import MerriamAPI from '../services/MerriamAPI';


class GetWordContainer extends Component {

  state = {
    word: {},
  }

  async componentDidUpdate() {
    try {
      const {data} = await MerriamAPI.getWordInfo(this.props.match.params.word);
      if (data) {
        this.setState({
          word: data,
        })
      }
      else {
        // TODO: Change to a Modal
        console.log("ERROR!");
      }
    }
    catch {
        console.log("ERROR!");
    }
    console.log(this.state.word)
  }


  render (){

    const {word, partOfSpeech, versions, pronunciation, shortdef} = this.state.word;

    return (

        <div>
          <Search
            handleUpdate={this.props.handleUpdate}
            setRedirect={this.props.setRedirect}
          />
          <div>
               {word}
          </div>
          <div>
               {partOfSpeech}
          </div>
          <div>
                {versions}
               {/* {this.props.versions.map((v, i) => <div key={i}>{v}</div>)} */}
          </div>
          <div>
               {pronunciation}
          </div>
          <div>
               {shortdef}
          </div>
        </div>
    );
    }
  }

   export default GetWordContainer;



