import React, {Component} from 'react';
import Search from '../components/Search';
import {Switch, Route, Link, Redirect, withRouter} from 'react-router-dom';
import MerriamAPI from '../services/MerriamAPI';


class GetWordContainer extends Component {

  state = {
    word: {},
    partOfSpeech: [],
    versions: [],
    shortdef: "",
    pronunciation: "",
  }

  setWord = word =>
  this.setState({word});

  callApi = () => {
    return MerriamAPI.getWordInfo(this.state.word)
  }

  redirectTo = (word) => 
    `/definition/${word}`

  async componentDidMount(){
    try{
      const {data, errors} = await MerriamAPI.show(this.props.match.params.word)
      if (data) {
          this.setState({
            word: data,
            // versions: data[0].meta.stems,
            // shortdef: data[0].shortdef[0],
            // partOfSpeech: data[0].fl,
            // pronunciation: data[0].hwi.prs[0].mw,
        })
      }
      else{
        console.log("ERROR");
        console.log(errors);
      }
    }
    catch {
      console.log("ERROR")
    }
  }

render (){

  const {partOfSpeech, versions, shortdef, pronunciation} = this.state.word;

  return (
      <div>
        <Search 
        word={this.state.word}
        setWord={this.setWord}
        callApi={this.callApi}
        redirectTo={this.props.setRedirect}
        />
        {/* <div>
            {partOfSpeech}
        </div>
        <div>
            {versions.map((v, i) => <div key={i}>{v}</div>)}
        </div>
        <div>
            {pronunciation}
        </div>
        <div>
            {shortdef}
        </div> */}
      </div>
  );
  }
}

 export default GetWordContainer;
