import React, {Component} from 'react';
import Search from '../components/Search';
import addWord from '../components/addWord';
import MerriamAPI from '../services/MerriamAPI';

class GetWordContainer extends Component {

state = {
  word: this.props.word,
  info: this.props.info,
  partOfSpeech: this.props.parOfSpeech,
  versions: this.props.versions,
  shortdef: this.props.shortdef,
  pronunciation: this.props.pronunciation,
}

//componentDidMount
//page refresh
//take the param and set the state 

async componentDidMount() {
  const word = this.props.match.params.word;
  try{
    const data = await MerriamAPI.getWordInfo(word);
    if (data) {
      this.setState({
        word: word,
        info: data,
        versions: data[0].meta.stems,
        shortdef: data[0].shortdef[0],
        partOfSpeech: data[0].fl,
        pronunciation: data[0].hwi.prs[0].mw, 
       })
      }
    else {
      console.log("Error")
    }
  }
  catch {
    console.log("Error")
  }
  console.log(word);
}


async componentDidUpdate() {
  const word = this.props.match.params.word;
  if (word != this.state.word){
    try{
      const data = await MerriamAPI.getWordInfo(word);
      if (data) {
        this.setState({
          word: word,
          info: data,
          versions: data[0].meta.stems,
          shortdef: data[0].shortdef[0],
          partOfSpeech: data[0].fl,
          pronunciation: data[0].hwi.prs[0].mw, 
         })
        }
      else {
        console.log("Error")
      }
    }
    catch {
      console.log("Error")
    }
    }
  }


render (){

  return (

      <div>
        <Search
          handleUpdate={this.props.handleUpdate}
          setRedirect={this.props.setRedirect}
        />
        <div>
             {this.state.word}
        </div>
        <div>
             {this.state.partOfSpeech}
        </div>
        <div>
             {this.state.versions.map((v, i) => <div key={i}>{v}</div>)}
        </div>
        <div>
             {this.state.pronunciation}
        </div>
        <div>
             {this.state.shortdef}
        </div>
        <div>
          {(this.state.shortdef.length > 0) && <addWord />}
        </div>
      </div>
  );
  }
}

 export default GetWordContainer;



