import React, {Component} from 'react';


class Search extends Component {
  constructor(props){
    super(props);
      this.state = {
        word:'',
      };
      this.handleChange = this.handleChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange = (event) => {
    this.setState({word:event.target.value.toLowerCase()})
  }


  handleSubmit = (e) => {
      e.preventDefault();
      this.props.search(this.state.word);
    }


  render (){
      return (
        <form onSubmit={this.handleSubmit}>
        <div>
          <input type="text" onChange={this.handleChange} />
          <button type="submit">Search</button>
        </div>
        </form>
      );
    }
  }



  state = {
    error: '',
}


search = async (word) => {

    try{
        const data = await MerriamAPI.getWordInfo(word);

        if (data){
            this.props.handleUpdate({
                word: word,
                info: data,
                versions: data[0].meta.stems,
                shortdef: data[0].shortdef[0],
                partOfSpeech: data[0].fl,
                pronunciation: data[0].hwi.prs[0].mw, 
            });
        // () => {
            this.props.setRedirect({
                path: `/definition/${word}`,
                // path: `/definition/${word}`,  
            });
            // }
        }
        else{
            console.log('No Definition Found')
        }
    }
    catch{
        this.setState({error: 'No Definition Found'})
    }
    // console.log(this.props.setRedirect())
} 


export default Search;
