import React, {Component} from 'react';
import MerriamAPI from '../services/MerriamAPI';

class Search extends Component {

state = {
  word: ''
}

state = {
  info: {}
}

handleChange = (event) => {
  console.log(`Filter changed! ${event.target.value}`);
  this.setState({word:event.target.value.toLowerCase()})
}


handleSubmit = async (e) => {
  e.preventDefault();
  var word = this.state.word.trim();
  const data = await MerriamAPI.getWordInfo(word);
  this.setState({info: data})
  console.log(word)
}

render (){
  this.props = this.state.word;
  const {word} = this.props;

    return (
      <form onSubmit={this.handleSubmit}>
      <div>
        <input type="text" onChange={this.handleChange} value={word}/>
        <button type="submit">Search</button>
      </div>
      </form>
    );
  }
}

export default Search;
