render (){

  return (

      <div >
        <div className="searchWordPage">
        <Search
          handleUpdate={this.props.handleUpdate}
          setRedirect={this.props.setRedirect}
          setModal={this.props.setModal}
        />
        {this.state.error}
        </div>
        <div className="App">
        <h1 className="display-6">
          Definition
        </h1>
        <div>
             <b>word:</b> {this.state.word}
        </div>
        <div>
              <b>part of speech:</b> {this.state.partOfSpeech}
        </div>
        <div>
             <b>versions:</b>  {this.state.versions.map((v, i) => <div key={i}>{v}</div>)}
        </div>
        <div>
            <b>pronunciation:</b> {this.state.pronunciation}
        </div>
        <div>
            <b>definition:</b>  {this.state.shortdef}
        </div>
        <br/>
        {/* <button onClick={this.handleSubmit} type="submit">Save</button> */}
        <Fave isFave={props.isFave} handleSave={props.handleSave} />
        </div>
      </div>
  );
  }
}

