                isFave={this.props.faves.word=(this.props.word)}
                handleSave={() => this.props.handleSave(word)}
                onDetailsClick={() => this.handleDetailsClick(word)}
            />
        );


        return (
            <div className={styles['saved-background']}>
            <h1 className={styles['first-header']}>My Saved Words: {this.props.faves.length}</h1>
            {allWords}
            {console.log(this.props.faves)}
            {/* {console.log(`CURRENT WORD:${this.props.current.word}`)} */}
            <WordDetails currentWord={this.state.current} />
