import React, {Component} from 'react';
import Search from '../components/Search';
import {Switch, Route, Link, Redirect} from 'react-router-dom';
import MerriamAPI from '../services/MerriamAPI';


class GetWordContainer extends Component {

  state = {
    error: '',
    word: this.props.word,
    partOfSpeech: this.props.partOfSpeech,
    versions: this.props.versions,
    shortdef: this.props.shortdef,
    pronunciation: this.props.pronunciation
  }

  redirectTo = () =>
     `/definition/${this.state.word}`


  search = async (word) => {

      try{
          // var word = this.state.word.trim();
          const data = await MerriamAPI.getWordInfo(word);

          if (data){
              // console.log(data)
              this.props.handleUpdate({
                  word: word,
                  info: data,
                  versions: data[0].meta.stems,
                  shortdef: data[0].shortdef[0],
                  partOfSpeech: data[0].fl,
                  pronunciation: data[0].hwi.prs[0].mw, 
                  // redirect:data.history.push(`/definition/${word}`)
              }, 
              // () => {
              //     this.props.history.push(`/definition/${word}`);
              // }
              // () => {
              //     this.props.setRedirect({
              //         path: this.props.redirectTo(data)
              //     });
              // }
              );
          }
          else{
              console.log('No Definition Found')
          }
      }
      catch{
          this.setState({error: 'No Definition Found'})
      }

  }


render (){

  //destructure props here  

  console.log(this.props)

  return (

      <div>
        <Search search={this.search} redirectTo={this.redirectTo}/>
        <div>
             {this.state.word}
        </div>
        <div>
             {this.state.partOfSpeech}
        </div>
        <div>
             {this.state.versions.map((v, i) => <div key={i}>{v}</div>)}
        </div>
        <div>
             {this.state.pronunciation}
        </div>
        <div>
             {this.state.shortdef}
        </div>
      </div>
  );
  }
}

 export default GetWordContainer;
