import React, {Component} from 'react';
import MerriamAPI from '../services/MerriamAPI';

class Search extends Component {


  state = {
    word:'',
    errors: [],
  };


handleChange = (event) => {
this.setState({word:event.target.value.toLowerCase()});
}


handleSubmit = (e) => {
  e.preventDefault();
  this.search(this.state.word);
}


search = async (word) => {

  try{
      const data = await MerriamAPI.getWordInfo(word);

      if (data){
          this.props.handleUpdate({
              word: word,
              info: data,
              versions: data[0].meta.stems,
              shortdef: data[0].shortdef[0],
              partOfSpeech: data[0].fl,
              pronunciation: data[0].hwi.prs[0].mw, 
          });
          this.props.setRedirect({
              path: `/definition/${word}`,
          });
      }
      else{
          this.setState({errors: 'Word not found'});
      }
  }
  catch{
      this.props.setModal('Offline')
  }
}



render (){
  return (
    <form  className="container h-100 d-flex justify-content-center jumbotron"  onSubmit={this.handleSubmit}>
        {/* {
          !this.state.errors.length ? null :
          <div>
            <h2>{this.state.errors.length} error(s) prevented this word from being found</h2>

            <ul>
              { this.state.errors.map( (m, i) => <li key={i}>{m}</li> ) }
            </ul>
          </div>
        } */}
    <div>
      <input type="text" onChange={this.handleChange} />
      <button type="submit">Search</button>
    </div>
    </form>
  );
}
}





