import React, {Component} from 'react';
import {Switch, Route, Link, Redirect} from 'react-router-dom';
import MerriamAPI from '../services/MerriamAPI';


class Search extends Component {

  state = {
      errors: []
  }

  handleChange = (e) => {
    this.props.setWord(e.target.value)
      // console.log(this.props.word);
    }


  handleSubmit = async e => {
      e.preventDefault();
      try{
        // var word = this.state.word.trim();
        const {data, errors} = await MerriamAPI.this.props.callApi();
        if (data){
          this.props.setRedirect({
            path: this.props.redirectTo(data)
          });
          console.log("YAY")
        }
        else{
            this.setState({errors})
        }
    }
    catch{
        console.log("ERROR")
    }
    // console.log(this.state.info)
    console.log(this.props.callApi())
  } 


  render (){
    const {word} = this.props
      return (
        <form onSubmit={this.handleSubmit}>
        <div>
          <input word={word} type="text" onChange={this.handleChange} />
          <button type="submit">Search</button>
        </div>
        </form>
      );
    }
  }






