import React, {Component} from 'react';
import Search from '../components/Search';
import MerriamAPI from '../services/MerriamAPI';


class GetWordContainer extends Component {

  state = {
    word: '',
  }

  async componentDidMount() {
    try {
      const {data} = await MerriamAPI.getWordInfo(this.props.match.params.id);
      if (data) {
        this.setState({word: data})
      }
      else {
        // TODO: Change to a Modal
        console.log("ERROR!");
      }
    }
    catch {
        console.log("ERROR!");
    }
  }


  render (){

    return (

        <div>
          <Search
            handleUpdate={this.props.handleUpdate}
            setRedirect={this.props.setRedirect}
          />
          <div>
               {this.props.match.params.word}
          </div>
          <div>
               {this.props.partOfSpeech}
          </div>
          <div>
               {this.props.versions.map((v, i) => <div key={i}>{v}</div>)}
          </div>
          <div>
               {this.props.pronunciation}
          </div>
          <div>
               {this.props.shortdef}
          </div>
        </div>
    );
    }
  }

   export default GetWordContainer;



