import React, {Component} from 'react';
import Search from '../components/Search';
import MerriamAPI from '../services/MerriamAPI';

class GetWordContainer extends Component {

state = {
   //only updataes state the first time
  // word: this.props.word,
  word: '',
  // info: [],
  // partOfSpeech: [],
  // versions: [],
  // shortdef: "",
  // pronunciation: "",
}

async componentDidMount() {
  await console.log(this.state.word)
}

// async componentDidMount() {
//   await console.log(this.state.word)
// }

componentWillReceiveProps() {
  this.setState({ word: this.props.word });  
}


render (){

  return (

      <div>
        <Search
          handleUpdate={this.props.handleUpdate}
          setRedirect={this.props.setRedirect}
        />
        <div>
             {this.props.word}
             {/* {this.props.match.params.word} */}
        </div>
        <div>
             {this.props.partOfSpeech}
        </div>
        <div>
             {this.props.versions.map((v, i) => <div key={i}>{v}</div>)}
        </div>
        <div>
             {this.props.pronunciation}
        </div>
        <div>
             {this.props.shortdef}
        </div>
      </div>
  );
  }
}

 export default GetWordContainer;



